let age = 16;

if (age >= 18) {
    console.log("You can vote!");
} else {
    console.log("You're too young to vote yet.");
}

// Output: You're too young to vote yet.

weather = "sunny"

if weather == "sunny":
    print("Let's go to the park!")
elif weather == "rainy":
    print("Let's stay inside and read.")
else:
    print("Let's see what the day brings!")

# Output: Let's go to the park!

let score = 85;

if (score >= 90) {
    console.log("Grade: A - Excellent!");
} else if (score >= 80) {
    console.log("Grade: B - Good job!");
} else if (score >= 70) {
    console.log("Grade: C - Keep trying!");
} else if (score >= 60) {
    console.log("Grade: D - Need improvement");
} else {
    console.log("Grade: F - Let's study more");
}

// Output: Grade: B - Good job!

let age = 15;
let hasParent = true;
let movieRating = "PG-13";

// Using AND operator
if (age >= 13 && movieRating === "PG-13") {
    console.log("You can watch this movie!");
}

// Using OR operator
if (age >= 17 || hasParent) {
    console.log("You can watch R-rated movies!");
}

// Using NOT operator
if (!hasParent && age < 17) {
    console.log("You need a parent for R-rated movies.");
}

// Count from 1 to 5
for (let i = 1; i <= 5; i++) {
    console.log("Count: " + i);
}

// Output:
// Count: 1
// Count: 2
// Count: 3
// Count: 4
// Count: 5

let secretNumber = 7;
let guess = 0;
let attempts = 0;

while (guess !== secretNumber) {
    // In a real program, you'd get input from user
    guess = Math.floor(Math.random() * 10) + 1;
    attempts++;

    if (guess === secretNumber) {
        console.log(`Correct! Found ${secretNumber} in ${attempts} attempts`);
    } else {
        console.log(`Guess ${guess} is wrong. Try again!`);
    }
}

let day = "Monday";
let weather = "sunny";

if (day === "Monday" || day === "Wednesday" || day === "Friday") {
    console.log("It's a school day!");

    if (weather === "sunny") {
        console.log("Perfect for outdoor PE class!");
    } else if (weather === "rainy") {
        console.log("Indoor activities today.");
    }

    // Loop through class schedule
    let classes = ["Math", "Science", "English", "Art"];
    for (let i = 0; i < classes.length; i++) {
        console.log(`Period ${i + 1}: ${classes[i]}`);
    }
} else {
    console.log("It's the weekend! Time to relax.");
}

