let name = "Alice";
let age = 25;
let isStudent = true;

console.log(name);     // Prints: Alice
console.log(age);      // Prints: 25
console.log(isStudent); // Prints: true

let wholeNumber = 42;        // Integer
let decimalNumber = 3.14;    // Float/Decimal
let negativeNumber = -10;    // Negative number

// Math operations
let sum = wholeNumber + decimalNumber;
let product = wholeNumber * 2;

console.log(sum);      // Prints: 45.14
console.log(product);  // Prints: 84

let firstName = "Alice";
let lastName = "Johnson";
let greeting = "Hello, world!";

// Combining strings
let fullName = firstName + " " + lastName;
let personalGreeting = "Hi, " + firstName + "!";

console.log(fullName);         // Prints: Alice Johnson
console.log(personalGreeting); // Prints: Hi, Alice!

let isLoggedIn = true;
let hasFinishedHomework = false;
let isWeekend = true;

// Booleans in action
if (isLoggedIn) {
    console.log("Welcome back!");
}

if (hasFinishedHomework) {
    console.log("Great job!");
} else {
    console.log("Time to do homework!");
}

let fruits = ["apple", "banana", "orange"];
let numbers = [1, 2, 3, 4, 5];
let mixedArray = ["Alice", 25, true];

// Accessing array items
console.log(fruits[0]);    // Prints: apple
console.log(numbers[2]);   // Prints: 3

// Adding items
fruits.push("grape");
console.log(fruits);       // Prints: ["apple", "banana", "orange", "grape"]

// Good variable names (descriptive and clear)
let studentName = "Alice";
let testScore = 95;
let isHomeworkComplete = true;
let shoppingList = ["milk", "bread", "eggs"];

// Bad variable names (unclear and confusing)
let x = "Alice";        // What does x represent?
let num = 95;           // What kind of number?
let flag = true;        // What is this flag for?
let arr = ["milk"];     // What kind of array?

let score = 0;
console.log("Starting score:", score);  // Prints: 0

// Player gets points
score = score + 10;
console.log("After bonus:", score);     // Prints: 10

// Player gets more points
score += 5;  // Shorthand for score = score + 5
console.log("Final score:", score);     // Prints: 15

// Update text variables too
let message = "Hello";
message = message + ", World!";
console.log(message);                   // Prints: Hello, World!

// Numbers vs Strings - they behave differently!
let numberA = 5;
let numberB = 3;
let stringA = "5";
let stringB = "3";

console.log(numberA + numberB);  // Prints: 8 (math addition)
console.log(stringA + stringB);  // Prints: "53" (text combination)

// Converting between types
let textNumber = "42";
let realNumber = Number(textNumber);
console.log(realNumber + 8);     // Prints: 50
